package experiments

import GA
val ex3str =
    ""
val ex3key = "thisisasupersecurepassword"
fun main() {

    Test(
        GA(
            encryptedString = ex3str,
            popSize = 1000,
            maxKeySize = 26,
            origMutationRate = 0.2,
            crossOverRate = 0.9,
            elitismRatio = 0.1,
            maxGen = 300,
            eliminateWorst = true,
            mutationType = Individual.MutationType.SCRAMBLE_INSERTION,
            tournamentSelectionType = GA.TournamentSelectionType.WEIGHTED
        ), givenKey = ex3key
    )

    // Generation: 100 Population: 3000 Min fitness prev gen: 0.3661494017869234 for ttisjoesueesriefeereefspwe
    // Generation: 157 Population: 3000 Min fitness prev gen: 0.09086933734342455 for thvsisasupersecurepassword

    // Parameters: popSize: 3000 crossOverRate: 0.9 maxKeySize: 26 maxGen: 200 selectionSampleSize: 3 mutationRate: 0.1 bestSelectionRatio: 0.3
    // Generation: 174 Population: 3000 Min fitness prev gen: 0.08522863787934183 for thcsisasucersecurepassword
}