package experiments

import GA
val ex3str =
    ""
val ex3key = "thisisasupersecurepassword"
fun main() {
    Test(
        GA(
            encryptedString = ex3str,
            popSize = 1000,
            maxKeySize = 26,
            origMutationRate = 0.2,
            crossOverRate = 0.9,
            elitismRatio = 0.1,
            maxGen = 300,
            eliminateWorst = true,
            mutationType = Individual.MutationType.SCRAMBLE_INSERTION,
            tournamentSelectionType = GA.TournamentSelectionType.WEIGHTED,
            monogram = true,
            bigram = false,
            trigram = false,
            quadgram = false,
            quintgram = false
        ), givenKey = ex3key
    )
}